package Basics;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String [] a = {"sunday","monday","friday"};

		for (String r : a) {
			System.out.println(r);
		}
		
		int [] i = {1,2,3,4,5};
		for (int j : i) {
			System.out.println(j);
		}
	}

}
